<script>

      var contactusel = document.getElementById('Toastcontact');
      var contactToast = bootstrap.Toast.getOrCreateInstance(contactusel); 
      $("#contactus").submit(function (e) {
            e.preventDefault();
            var form = $('#contactus');
            var action = form.attr('action'); 
            
            $.ajax({
            type: "POST",
            url: action,
            data: form.serialize(), // serializes the form's elements.
            success:function(response){
                // window.swal("Success", response.msg, "success")
                // .then(function(value) {
                //             location.reload();
                //         });
                contactToast.show()

                
                $("#submitContactus").attr("disabled","disabled");
                setTimeout(() => {
                  
                  $("#submitContactus").prop("disabled",false);
                }, 3000);
             
            },
            error:function(requestObject){
                    console.log(requestObject);
                    window.swal("Oops!", requestObject.responseJSON.errorMsg.errorInfo[2], "error")
                    .then(function(value) {
                        });
                    
                    
                        

            }
            });
        });
  $( '.js-input' ).keyup(function() {
  if( $(this).val() ) {
     $(this).addClass('not-empty');
  } else {
     $(this).removeClass('not-empty');
  }
});
</script>


